import React from 'react';
import { View, Text, StyleSheet, Button, TouchableOpacity } from 'react-native';
const Circle = (props) => {

   return (
       <View>
       <TouchableOpacity style={{ display: "none", backgroundColor: props.color, borderRadius: 300 / 2, width: props.size, height: props.size, display:"flex", justifyContent: "center", alignItems: "center"}}>
           <Text style={styles.bubbleName}>{props.name}</Text>
       </TouchableOpacity>
       <TouchableOpacity style={{backgroundColor: props.color, borderRadius: 400 / 2, width: 400, height: 400, display:"flex", justifyContent: "center", alignItems: "center"}}>
           <Text>4 stories recorded</Text>
           <Text>{props.name}</Text>
           <Text>Fear refers to the irrational status that is caused by an unpleasant condition.</Text>
           <TouchableOpacity>
               <Text>Learn More</Text>
           </TouchableOpacity>
       </TouchableOpacity>
       </View>

   );
}
export default Circle;

const styles = StyleSheet.create({
bubbleName: {
    color: "white",
    justifyContent: "center",
    alignItems: "center",
    fontSize: 10
  },

});
