import React, {useState, useEffect} from 'react';
import { View, Text, StyleSheet, Button, TouchableOpacity } from 'react-native';
const DiaryEntry = (props) => {

   return (
       <View style={{backgroundColor: "white", marginTop: 20, padding: 20, borderRadius: 10}}>
           <Text style={{color: props.colors[props.trigger], fontSize: 16}}>{props.title}</Text>
           <Text>{props.trigger} &middot; {props.date}</Text>
           <Text>{props.story.substring(0, 90)}...</Text>
       </View>

   );
}
export default DiaryEntry;

